INSERT INTO `tbl_content_item_value` VALUES ('5840', '428', 'It has the most natural way to bottle feed.<br>BPA free polypropylene material is easy to sterilize.<br>It has been clinically proven to reduce colic and discomfort.', '0', '2016-11-22 11:37:30', '2016-11-22 11:37:30');
INSERT INTO `tbl_content_item_value` VALUES ('5841', '428', 'A range of flow rates are available for your growing baby.<br>Soft and wide shaped nipple promotes natural latch on.<br>Natural nipple shape makes it easy for baby to combine with breastfeeding.<br>', '0', '2016-11-22 11:37:30', '2016-11-22 11:37:30');
INSERT INTO `tbl_content_item_value` VALUES ('5842', '428', 'Twin valves prevent babies from swallowing air to reduce colic.<br>Wide neck opening makes it easy to fill and clean.<br>Nipple is soft and breast shaped to promote comfortable latch on.', '0', '2016-11-22 11:37:30', '2016-11-22 11:37:30');
INSERT INTO `tbl_content_item_value` VALUES ('5843', '428', 'It has the most natural way to bottle feed.<br>The nipple can sense baby\'s pressure.<br>Micro air vents help to prevent or reduce nipple collapse and minimize air ingestion.', '0', '2016-11-22 11:37:30', '2016-11-22 11:37:30');
INSERT INTO `tbl_content_item_value` VALUES ('5844', '428', 'It is easy to read scale for accurate measuring.<br>It is easy to fill.<br>Wide mouth system made to be as close to breastfeeding as possible.', '0', '2016-11-22 11:37:30', '2016-11-22 11:37:30');
INSERT INTO `tbl_content_item_value` VALUES ('5845', '428', 'Orthodontic nipple is silky soft silicone.<br>A range of flow rates are available for your growing baby.<br>The bottles with anatomic nipples have soft and flexible, custom flow nipples.', '0', '2016-11-22 11:37:30', '2016-11-22 11:37:30');
INSERT INTO `tbl_content_item_value` VALUES ('5846', '428', 'It is durable.<br>Anti colic valve means less gas for baby.<br>Advanced anti colic system is directly integrated into the nipple with no extra hard to clean parts.', '0', '2016-11-22 11:37:30', '2016-11-22 11:37:30');
