INSERT INTO `tbl_content_item_value` VALUES ('9150', '604', 'A premium drop resistant and water resistant flashlight renowned for its quality, durability and reliability.<br>Fits into any glove compartment, backpack, emergency kit, night stand.<br>Anti-reflective aluminum alloy coating makes it durable for long-term use.', '0', '2016-11-22 17:54:07', '2016-11-22 17:54:07');
INSERT INTO `tbl_content_item_value` VALUES ('9151', '604', 'Skid-proof and water resistant design, best suitable for outdoor camping, climbing, cycling, etc.<br>It is incredibly versatile, and even great for self-defense.<br>Never worry about getting stuck in the dark.', '0', '2016-11-22 17:54:07', '2016-11-22 17:54:07');
INSERT INTO `tbl_content_item_value` VALUES ('9152', '604', 'Perfect for cycling, hiking, camping and other outdoor activities.<br>Its compact design and balanced weight-to-size ratio make it ideal for everyone.<br>It is lightweight and easy to carry and store.', '0', '2016-11-22 17:54:07', '2016-11-22 17:54:07');
INSERT INTO `tbl_content_item_value` VALUES ('9153', '604', 'Anti-reflective aluminum alloy coating makes it durable for long-term use.<br>It is easy to grab with a waist strap.<br>Aluminum alloy casing with O-ring for waterproof and shockproof design.', '0', '2016-11-22 17:54:07', '2016-11-22 17:54:07');
INSERT INTO `tbl_content_item_value` VALUES ('9154', '604', 'This flashlight is easy-to-clean,you can clean by cloth.<br>Adjustable focus range for different usage.<br>Compact and lightweight to easily be stored in a pocket or glove compartment.', '0', '2016-11-22 17:54:07', '2016-11-22 17:54:07');
