INSERT INTO `tbl_content_item_value` VALUES ('9247', '615', 'The slip openings is good for connecting to male unthreaded pipes or fitting.<br>It features good ductility and resistance to high pressures and temperatures.<br>The pure virgin vinyl used in the products lasts longer and works better.', '0', '2016-11-22 17:54:11', '2016-11-22 17:54:11');
INSERT INTO `tbl_content_item_value` VALUES ('9248', '615', 'It is suitable for both hot and cold water pressure distribution systems.<br>It will not crack or change color.<br>It has female slip openings for sliding over the ends of male unthreaded pipes.', '0', '2016-11-22 17:54:11', '2016-11-22 17:54:11');
INSERT INTO `tbl_content_item_value` VALUES ('9249', '615', 'It has slip connections.<br>Glossy surface finish with tapered sockets makes it a professional appearance.<br>It connects two pipes at an angle to change the direction of flow between pipes.', '0', '2016-11-22 17:54:11', '2016-11-22 17:54:11');
INSERT INTO `tbl_content_item_value` VALUES ('9250', '615', 'It will not degrade over time.<br>It adapts pressure pipe to standard female pipe threads.<br>It can change the direction of piping in many different industries.', '0', '2016-11-22 17:54:11', '2016-11-22 17:54:11');
INSERT INTO `tbl_content_item_value` VALUES ('9251', '615', 'It can be used both in residential and commercial applications.<br>It is made of PVC.<br>Smooth interior finish reduces friction and material buildup.', '0', '2016-11-22 17:54:11', '2016-11-22 17:54:11');
INSERT INTO `tbl_content_item_value` VALUES ('9252', '615', 'It is ideal for irrigation and underground residential sprinkler systems.<br>Pipe fittings are components used for connecting, terminating and controlling flow.<br>It reduces friction and inhibits buildup of materials.', '0', '2016-11-22 17:54:11', '2016-11-22 17:54:11');
INSERT INTO `tbl_content_item_value` VALUES ('9253', '615', 'It is durable and lightweight.<br>It is made of PVC material.<br>It is suitable for irrigation, pool and spa, potable water, and chilled water applications.', '0', '2016-11-22 17:54:11', '2016-11-22 17:54:11');
