DELIMITER ;;
CREATE PROCEDURE `AddSon`(IN nid INT,  
   IN CNname varchar(50) character set utf8,
   IN ENname varchar(50),   
   IN rem  varchar(250),
   IN cateid INT)
BEGIN
DECLARE rgt INT;
DECLARE reg INT;
DECLARE rig INT;
DECLARE ids INT;
DECLARE nodeid INT;
DECLARE t_error INTEGER DEFAULT 0;  
DECLARE CONTINUE HANDLER FOR SQLEXCEPTION SET t_error=1;  
IF exists(SELECT id FROM tbl_product_gallery WHERE id = nid)  THEN
    SET nodeid=1;
    SET rig =(SELECT right_id FROM tbl_product_gallery WHERE id=nodeid);
    START TRANSACTION;
        SET rgt = (SELECT  right_id FROM tbl_product_gallery  WHERE id = nid );
        UPDATE tbl_product_gallery  SET right_id = right_id + 2 WHERE right_id >= rgt;  
        UPDATE tbl_product_gallery  SET left_id = left_id + 2 WHERE left_id >= rgt ;
        SET reg=(SELECT  right_id FROM tbl_product_gallery  WHERE id =nodeid );
    IF t_error = 1 THEN
      ROLLBACK;
    END IF;
    IF  reg!=rig+2 THEN
        ROLLBACK;
    END IF;
    COMMIT;
END IF;
SELECT id FROM  tbl_product_gallery WHERE left_id=rgt;
END
;;
DELIMITER ;

