-- ----------------------------
-- Procedure structure for `imageMove`
-- ----------------------------
DROP PROCEDURE IF EXISTS `imageMove`;
DELIMITER ;;
CREATE PROCEDURE `imageMove`( move_id INT, mid INT)
BEGIN
DECLARE lft INT;
DECLARE rgt INT;
DECLARE reg INT;
DECLARE namecn VARCHAR(50);
DECLARE nameen VARCHAR(50);
DECLARE rem VARCHAR(250);
DECLARE num INT;
DECLARE sta INT;
DECLARE i INT;
DECLARE nid INT;
DECLARE pid INT;
DECLARE ids INT;
DECLARE t_error INT DEFAULT 0;
DECLARE CONTINUE HANDLER FOR SQLEXCEPTION SET t_error=1;
IF EXISTS(SELECT id FROM tbl_product_gallery WHERE id = move_id)  THEN
   START TRANSACTION;
       SET lft=(SELECT left_id FROM tbl_product_gallery WHERE id = move_id);
       SET rgt=(SELECT right_id FROM tbl_product_gallery WHERE id = move_id);
       CREATE TEMPORARY TABLE tem_move SELECT * FROM imageview WHERE left_id>=lft AND right_id<=rgt ORDER BY Layer ASC;
       DELETE FROM tbl_product_gallery WHERE left_id>=lft AND right_id <=rgt;
       SET num=rgt-lft+1;
       UPDATE tbl_product_gallery SET left_id = left_id - num WHERE left_id > rgt;
       UPDATE tbl_product_gallery SET right_id = right_id - num WHERE right_id > rgt;
       SET i=0;
       SET sta=rgt-lft;
       SET nid=mid;
          WHILE i<=sta DO
              IF EXISTS(SELECT id FROM tem_move WHERE left_id=lft) THEN
                  SET  nameen=(SELECT en_name FROM tem_move WHERE left_id=lft);
                  SET  namecn=(SELECT cn_name FROM tem_move WHERE left_id=lft);
                  SET  rem=(SELECT remark FROM tem_move WHERE left_id=lft);
                  SET  ids=(SELECT id FROM tem_move WHERE left_id=lft);
                  IF (SELECT getImageParentNodeid(ids)) THEN
                     SET pid=getImageParentNodeid(ids);
                     SET nameen=(SELECT en_name FROM tem_move WHERE id=pid);
                     SET nid=(SELECT id FROM tbl_product_gallery WHERE en_name=nameen);
                  END IF;
                  CALL AddSon(nid,namecn,nameen,rem);
            END IF;
       SET lft=lft+1;
       SET i=i+1;
        END   WHILE;
        IF t_error=1 THEN
          ROLLBACK;
         END IF;
        SET pid=getImageParentId(nameen);
        SET reg=(SELECT right_id FROM tbl_product_gallery WHERE id=pid);
        SET rgt=(SELECT right_id FROM tbl_product_gallery WHERE en_name=nameen);
        SET lft=(SELECT left_id FROM tbl_product_gallery WHERE en_name=nameen);
        IF reg!=rgt+1 AND rgt!=lft+1 THEN
            ROLLBACK;
        END IF;
        COMMIT;
END IF;        
SELECT id FROM tbl_product_gallery where id=move_id;
DROP TABLE tem_move;
END
;;
DELIMITER ;

